import React from 'react';
import ReactDOM from 'react-dom'
import {deleteMessage} from '../actions'
import { connect } from 'react-redux';
import axios from 'axios';
class Detail extends React.Component
{
    buttonClick () 
    {
        this.props.deleteMessage(this.props.info, this.setState({state: this.state}))
        var object = {
            message: this.state.info,
            date: new Date(),
            length: this.state.info.length

        }
        axios.delete("http://localhost:3000/messages", object)
        .then(response => {
            console.log(response.data);
        })

    }
    render()
    {
        return ReactDOM.createPortal(
        <div className='popup'>
            <h>Date Added:
            {this.props.date}</h>
            <h>Length of message: 
            {this.props.length}</h>
            <h>Message:
           {this.props.info}</h>
           <button onClick = {()=> this.props.deleteMessage(this.props.info, this.setState({state: this.state}))} >delete</button>
        </div>, document.getElementById("root")
        )

    };
}
const mapStateToProps = (state) => {
    return { 
    messages: state.messages
};
}


export default connect(mapStateToProps, { deleteMessage })(Detail);
