import React from 'react';
import Message from './Message';
import { addMessage } from '../actions'
import { connect } from 'react-redux';
import Detail from './Detail';
import axios from 'axios'
import ReactCSSTransitionGroup from 'react-transition-group'; // ES6

class MessageBox extends React.Component{
    constructor(props){
        super(props);    
        axios.get("http://localhost:3000/messages")
        .then(response => {

            console.log(response.data)
            response.data.forEach(message=> {

                this.props.addMessage(message);

                this.setState({ state: this.state })

            });
        })
        .catch(error =>{
            console.log(error);
        })

        }
        buttonClick = () => {

            var object = {
                message: this.state.text,
                date: new Date(),
                length: this.state.text.length

            }

            axios.post("http://localhost:3000/messages", object)
            .then(response => {
                this.props.addMessage(response.data)
                 this.setState({ state: this.state })
            })



        }
        sortMessages = (criteria) =>
        {
            console.log("yeet")

        }
    render()
    {
        return(
            <div >
                <form>
                    Sort by:
                    <select>
                    <label>sort by</label>
                    <option value="length">length</option>
                    <option value= "date">date</option>
                    </select>
                    <br></br>
                    <button onClick={this.sortMessages(this.value)}>sort</button>
                </form>
           <div id="messageBox">
            {this.props.messages.map((item, key) => <Message _id={item._id}  date={item.date} length={item.length} text={item.message}/>)}
           </div>
           <textarea value = {this.setState.value} onChange={(event) => this.setState({text: event.target.value})} id="input"></textarea>
            <button onClick= {this.buttonClick}>add message</button>
           </div>
        )
    }
}
const mapStateToProps = (state) => {
    return { 
    messages: state.messages
};
}


export default connect(mapStateToProps, { addMessage })(MessageBox);
