import React from 'react';
import Message from './Message';
import { addMessage, clearMessages } from '../actions'

import { connect } from 'react-redux';
import Detail from './Detail';
import axios from 'axios'
import ReactCSSTransitionGroup from 'react-transition-group'; // ES6

class MessageBox extends React.Component{
    constructor(props){
        super(props);    
        axios.get("http://localhost:3000/messages")
        .then(response => {

            console.log(response.data)
            response.data.forEach(message=> {

                this.props.addMessage(message);

                this.setState({ state: this.state })

            });
        })
        .catch(error =>{
            console.log(error);
        })

        }
        buttonClick = () => {

            var object = {
                message: this.state.text,
                date: new Date(),
                length: this.state.text.length

            }

            axios.post("http://localhost:3000/messages", object)
            .then(response => {
                this.props.clearMessages();
                 this.setState({ state: this.state })
            })



        }
   clearMessages = () =>
   {
        axios.delete("http://localhost:3000/messages/clear")
        .then(response => {
            this.props.messages.clear();
            this.setState({ state: this.state })
        })
   }
    render()
    {
        return(
            <div >
           <div id="messageBox">
            {this.props.messages.map((item, key) => <Message _id={item._id}  date={item.date} length={item.length} text={item.message}/>)}
           </div>
           <textarea value = {this.setState.value} onChange={(event) => this.setState({text: event.target.value})} id="input"></textarea>
            <button onClick= {this.buttonClick}>add message</button><button onClick={()=>{this.clearMessages()}}>clear messages</button>
           </div>
        )
    }
}
const mapStateToProps = (state) => {
    return { 
    messages: state.messages
};
}


export default connect(mapStateToProps, { addMessage,clearMessages })(MessageBox);
