import React from 'react';
import { connect } from 'react-redux';
import { increment } from '../actions';
import { decrement } from '../actions';
import { addMessage } from '../actions'

class Button extends React.Component {
    render() 
    {
        return (<div>
            <h1>the number is: {this.props.count}</h1>
            <button onClick={() => this.props.increment(5)}> increment</button>
            <button onClick={()=> this.props.decrement(5)}>decrement</button>
            <textarea id="input"></textarea>
            <button onClick= {() => this.props.addMessage(document.getElementById("input").value)}>add message</button>
            </div>);
    }
}
const mapStateToProps = (state) => {
    return { count: state.count,
    messages: state.messages
};
}

export default connect(mapStateToProps, { increment, decrement, addMessage })(Button);
