import React from 'react';
import Detail from './Detail'

class Message extends React.Component{
    render()
    {
        return(
        <div><button className="messageButton" onClick={()=> <Detail info={this.props.text}/>}><p>{this.props.text}</p></button></div>
        )
    }

}

export default Message;
